Rules.InvariantScript:
Set(varRootPath, "Content Templates/");
Set(varActiveFolder, varRootPath);

ClearCollect(
    ContentLibrary,
    Filter(
        'Content Templates',
        StartsWith('Folder path', varRootPath)
    )
);

ClearCollect(
    colDocPanelCrumbs,
    Table(
        {
            Title: "",
            Path: ""
        }
    )
);
Clear(colDocPanelCrumbs);
With(
    {
        folderParts: Split(Left(varRootPath, Len(varRootPath) - 1), "/")
    },
    Collect(
        colDocPanelCrumbs,
        {
            Title: Text(Last(folderParts).Value),
            Path: varRootPath
        }
    )
);

ClearCollect(colKBCatPick, { Cat: "" });
Clear(colKBCatPick);

ClearCollect(colCatPick, { Value: "" });
Clear(colCatPick);

Concurrent(
    Set(varKBOpen, false),
    Set(varKBMode, "View"),
    Set(varKBCurrent, Blank()),
    ClearCollect(
        colMenu,
        { ID: 1, Title: "Launch Pad", ScreenName: LaunchPad },
        { ID: 2, Title: "Brain Stash", ScreenName: BrainStash },
        { ID: 3, Title: "The Pantry", ScreenName: ThePantry }
    )
);

ClearCollect(
    colCategoryStrings,
    ForAll(
        Filter(
            ContentLibrary,
            'Folder path' = varActiveFolder,
            !IsBlank(Category.Value)
        ),
        { CategoryText: Category.Value }
    )
);

ControlPropertyState.AutoRuleBindingString:
// seed schema for the component's crumb list (avoids "name isn't valid" during authoring)
ClearCollect(colDocPanelCrumbs, { ID: 0, DisplayName: "" });
Clear(colDocPanelCrumbs);

ClearCollect(colKBCatPick, { Cat: "" });
Clear(colKBCatPick);

// 2) Independent setup (parallel)
Concurrent(
    Set(varKBOpen, false),
    Set(varKBMode, "View"),
    Set(varKBCurrent, Blank()),
    ClearCollect(
        colMenu,
         { ID: 1, Title: "Launch Pad", ScreenName: LaunchPad },
        { ID: 2, Title: "Brain Stash", ScreenName: BrainStash },
        { ID: 3, Title: "The Pantry",           ScreenName: ThePantry },
         { ID: 4, Title: "Client Depot",           ScreenName: ClientDepot},
          { ID: 5, Title: "Becca Bot",           ScreenName: BeccaBot }

    )
)
